pragma cashscript ^0.6.0;

contract P2PKH(bytes20 pkh) {
    function spend(pubkey pk, sig s) {
        require(hash160(pk) == pkh);
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
        require(checkSig(s, pk));
    }
}
